mapper.delete:

delete from subject_record
where id = #{id,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}

mapper.update:

update subject_record
set right_count = #{rightCount,jdbcType=INTEGER},
  `type` = #{type,jdbcType=INTEGER},
  score = #{score,jdbcType=DECIMAL},
  create_time = #{createTime,jdbcType=DATE}
where id = #{id,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}

mapper.select:

select id, user_id, right_count, `type`, score, create_time
from subject_record
where id = #{id,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
